def create(weird=None):
  if not weird: weird=random.choice(abc)
  choice=''
  while choice!=' ':
      lieu=random.random()
      som=0
      try:
        dispo=prob[weird[-1]]
        choice=''
        for i in dispo:
          som+=dispo[i]
          if som>lieu:
            choice=i
            break
      except KeyError:
        choice=' '
      weird+=choice
  return weird[:-1]

